package main

import (
	"fmt"
	"math/big"
	"strings"
)

func main() {
	pi := "3."
	fmt.Println(pi)
	precision := uint(len(pi[strings.Index(pi, ".")+1:]))
	fmt.Printf("%d decimal digits!\n", precision)

	bf := big.NewFloat(0.0)
	bf, _ = bf.SetPrec(precision).SetMode(bf.Mode()).SetString(pi)
	fmt.Println(bf.Text('f', int(precision)))
}
